INSERT INTO "biographies" VALUES('4cae34b18222cf5d5790cb8277d15893','0e917798957413534b2f9a8889024079','Levi','levi-4cae34b1','上攀/攀岩攝影',NULL,'https://lh3.googleusercontent.com/a/ACg8ocIwsxpcjqDWzBRmJbpHnwk7Y7ggzYr-RhpnJXWTFVpg8VmOk3YHmA=s96-c','https://storage.nobodyclimb.cc/biography/6495458fd7923465409f89a8b0641b5b.jpg',NULL,'{"instagram":"Sky912745","youtube":"sky912745"}',1,'2026-01-16 05:42:22','2026-01-21 13:26:17','2020','龍洞/中和原岩','運動攀登, 傳統攀登, 屋簷, 裂隙, 外傾岩壁, 靜態, 耐力, 耐力型',NULL,'攀岩是我與社會連結的方式，帶給我一群可以交付生命一起努力的朋友',NULL,NULL,1,'2026-01-16T05:42:22.341Z',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,replace('有一次爬末代皇帝，起攀只能放很小又有點外開的岩械，下面是礁岩\n爬過第一個難關之後看了一下覺得怪怪的就下攀回到地面，請確保take受力測試\n結果就噴cam了...(撿回一命)','\n',char(10)),replace('中和原岩/台灣最北神秘小島\n原岩做為台灣攀岩界路線與氛圍最有品質的岩館，收費的價格相較於國外真的便宜很多，希望大家知足、珍惜','\n',char(10)),NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'牆上攝影',NULL,NULL,3,94,4,replace('看了嘉明湖的照片一個人報了商業團\n嚮導是小紘，他跟我介紹抱石我才知道這項運動門檻沒想像中的高\n但去了抱石館之後發現相較於又跑又跳還要用力，更喜歡上攀時的心流','\n',char(10)),'不要抱石，好好上攀',0,'public','{"selections":[{"tag_id":"sys_style_cult_crack","source":"system"},{"tag_id":"sys_injury_badge_none","source":"system"},{"tag_id":"sys_time_type_weekend","source":"system"},{"tag_id":"sys_time_type_night","source":"system"},{"tag_id":"sys_lifestyle_weekend","source":"system"},{"tag_id":"sys_climbing_music_gym","source":"system"},{"tag_id":"sys_failure_response_switch","source":"system"},{"tag_id":"sys_training_approach_zen","source":"system"},{"tag_id":"sys_local_identity_longdong","source":"system"},{"tag_id":"sys_local_identity_gym","source":"system"},{"tag_id":"sys_social_type_partner","source":"system"},{"tag_id":"usr_tag_1768838983495","source":"user"}],"custom_tags":[{"id":"usr_tag_1768838983495","source":"user","dimension_id":"sys_social_type","label":"#龍洞怪人","description":"東搞西搞","order":999}]}','{"climbing_meaning":{"answer":"攀岩是我與社會連結的方式，帶給我一群可以交付生命一起努力的朋友","visibility":"public"},"climbing_origin":{"answer":"看了嘉明湖的照片一個人報了商業團\n嚮導是小紘，他跟我介紹抱石我才知道這項運動門檻沒想像中的高\n但去了抱石館之後發現相較於又跑又跳還要用力，更喜歡上攀時的心流","visibility":"public"},"advice_to_self":{"answer":"不要抱石，好好上攀","visibility":"public"},"best_moment":{"answer":"克服路線恐懼與難關","visibility":"public"},"favorite_place":{"answer":"所有可以用繩子的地方","visibility":"public"},"current_goal":{"answer":"不要受傷不要受傷，佛系收線","visibility":"public"},"climbing_takeaway":{"answer":"人與人的距離可以用一條動力繩拉近","visibility":"public"},"climbing_style_desc":{"answer":"動態就不爬","visibility":"public"},"life_outside":{"answer":"龍洞金國威","visibility":"public"},"bucket_list":{"answer":"跟夥伴平安爬到不能爬為止","visibility":"public"}}','{"uncategorized":{"funny_moment":{"answer":"有一次爬末代皇帝，起攀只能放很小又有點外開的岩械，下面是礁岩\n爬過第一個難關之後看了一下覺得怪怪的就下攀回到地面，請確保take受力測試\n結果就噴cam了...(撿回一命)","visibility":"public","updated_at":"2026-01-20T16:46:38.027Z"},"favorite_spot":{"answer":"中和原岩/台灣最北神秘小島\n原岩是全台灣路線與氛圍最有品質的岩館，最推薦來這裡\n最北神秘小島的話，只推薦給喜歡探險，有能力掌控風險的人","visibility":"public","updated_at":"2026-01-20T16:46:38.027Z"},"memorable_moment":{"answer":"大禮堂的秘密開關\n難關是一個連續動作再翻天花板\n都做完之後突然眼前一片黑，身體開始無力\n馬上塞岩械take休息，但全身開始癱軟以及止不住的滴口水\n趁失去意識之前再補了一隻岩械趕快回到地面休息\n事後檢討應該是夏天汗流太多，加上當天沒吃什麼東西又都只喝水，電解質都被稀釋掉了\n現在如果要拼線都會標配一瓶運動飲料，不論想不想喝都得喝","visibility":"public","updated_at":"2026-01-20T16:46:38.027Z"},"biggest_challenge":{"answer":"剛開始攀岩時總是會覺得自己爬不上，過不去，自己很爛一直卡關\n每天只想變強，羨慕高手，整天都想跟別人比\n「跟別人比，比不完。」 －扁頭說\n也覺得這樣心態很不健康，如果只在意攀爬能力只會讓攀岩的壓力更大\n後來佛系攀岩，再也沒有遇到卡關的問題\n爬不過去就算了，就算了，開心最重要！","visibility":"public","updated_at":"2026-01-20T16:46:38.027Z"},"breakthrough_story":{"answer":"用傳攀把曙光裂隙爬掉\n\n這條路線對我意義重大，第一次到一洞時就被這壯觀的路線吸引\n原來第一顆bolt這麼遠是常態、原來岩質這麼差路線這麼長是常態\n從我運攀邊爬邊用掛繩器掛起來試動作\n到學完傳攀後，判斷保護點放置位置與評估風險\n終於一顆bolt都沒用，用傳攀把這條路線爬掉","visibility":"public","updated_at":"2026-01-20T16:46:38.027Z"}}}','{"name":"Levi","title":"上攀/攀岩攝影","bio":"","climbing_start_year":2020,"frequent_locations":"龍洞/中和原岩","home_gym":""}',NULL);
