INSERT INTO "bucket_list_items" VALUES('a2948989e8d58a834aed66e991f15316','b1cd382add6d110fdfefec231d6d83cd','完攀墾丁「自由之路」5.12a','outdoor_route','墾丁最經典的 5.12a 路線，擁有連續的 crimps 和需要爆發力的動作。30 米的長度對耐力也是極大考驗。','5.12a','墾丁','2025-12-31','active',NULL,1,'milestone',40,'[{"id":"1","title":"完成路線觀察和動作分析","percentage":20,"completed":true,"completed_at":"2024-10-15","note":"已經研究過整條路線，記下每個難點和休息點"},{"id":"2","title":"完攀前半段（到第 4 個 bolt）","percentage":40,"completed":true,"completed_at":"2024-12-05","note":"前半段技術性動作已經掌握，可以穩定通過"},{"id":"3","title":"通過 crux（中段連續 crimps）","percentage":70,"completed":false,"completed_at":null,"note":"這是最難的部分，需要更強的手指力量"},{"id":"4","title":"完攀整條路線","percentage":100,"completed":false,"completed_at":null,"note":"最終目標！"}]',NULL,NULL,NULL,NULL,1,0,0,0,0,'2026-01-14 11:44:43','2026-01-14 11:44:43');
