PRAGMA defer_foreign_keys=TRUE;
PRAGMA foreign_keys=OFF;
CREATE TABLE IF NOT EXISTS "users" (
  id TEXT PRIMARY KEY,
  email TEXT UNIQUE NOT NULL,
  username TEXT UNIQUE NOT NULL,
  password_hash TEXT,  
  display_name TEXT,
  avatar_url TEXT,
  bio TEXT,
  role TEXT DEFAULT 'user' CHECK (role IN ('user', 'admin', 'moderator')),
  is_active INTEGER DEFAULT 1,
  email_verified INTEGER DEFAULT 0,
  google_id TEXT UNIQUE,
  auth_provider TEXT DEFAULT 'local' CHECK (auth_provider IN ('local', 'google')),
  created_at TEXT DEFAULT (datetime('now')),
  updated_at TEXT DEFAULT (datetime('now')),
  climbing_start_year TEXT,
  frequent_gym TEXT,
  favorite_route_type TEXT
);
CREATE TABLE refresh_tokens (
  id TEXT PRIMARY KEY,
  user_id TEXT NOT NULL,
  token_hash TEXT NOT NULL,
  expires_at TEXT NOT NULL,
  created_at TEXT DEFAULT (datetime('now')),
  FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE
);
CREATE TABLE posts (
  id TEXT PRIMARY KEY,
  author_id TEXT NOT NULL,
  title TEXT NOT NULL,
  slug TEXT UNIQUE NOT NULL,
  excerpt TEXT,
  content TEXT NOT NULL,
  cover_image TEXT,
  status TEXT DEFAULT 'draft' CHECK (status IN ('draft', 'published', 'archived')),
  is_featured INTEGER DEFAULT 0,
  view_count INTEGER DEFAULT 0,
  published_at TEXT,
  created_at TEXT DEFAULT (datetime('now')),
  updated_at TEXT DEFAULT (datetime('now')), category TEXT,
  FOREIGN KEY (author_id) REFERENCES users(id) ON DELETE CASCADE
);
CREATE TABLE post_tags (
  post_id TEXT NOT NULL,
  tag TEXT NOT NULL,
  PRIMARY KEY (post_id, tag),
  FOREIGN KEY (post_id) REFERENCES posts(id) ON DELETE CASCADE
);
CREATE TABLE gyms (
  id TEXT PRIMARY KEY,
  name TEXT NOT NULL,
  slug TEXT UNIQUE NOT NULL,
  description TEXT,
  address TEXT,
  city TEXT,
  region TEXT,
  latitude REAL,
  longitude REAL,
  phone TEXT,
  email TEXT,
  website TEXT,
  cover_image TEXT,
  is_featured INTEGER DEFAULT 0,
  opening_hours TEXT, -- JSON string
  facilities TEXT, -- JSON array
  price_info TEXT, -- JSON object
  rating_avg REAL DEFAULT 0,
  review_count INTEGER DEFAULT 0,
  created_at TEXT DEFAULT (datetime('now')),
  updated_at TEXT DEFAULT (datetime('now'))
);
CREATE TABLE crags (
  id TEXT PRIMARY KEY,
  name TEXT NOT NULL,
  slug TEXT UNIQUE NOT NULL,
  description TEXT,
  location TEXT,
  region TEXT,
  latitude REAL,
  longitude REAL,
  altitude INTEGER,
  rock_type TEXT,
  climbing_types TEXT, -- JSON array: ["sport", "trad", "boulder"]
  difficulty_range TEXT, -- e.g., "5.6-5.13a"
  route_count INTEGER DEFAULT 0,
  bolt_count INTEGER DEFAULT 0,
  cover_image TEXT,
  images TEXT, -- JSON array
  is_featured INTEGER DEFAULT 0,
  access_info TEXT,
  parking_info TEXT,
  approach_time INTEGER, -- minutes
  best_seasons TEXT, -- JSON array
  restrictions TEXT,
  rating_avg REAL DEFAULT 0,
  review_count INTEGER DEFAULT 0,
  created_at TEXT DEFAULT (datetime('now')),
  updated_at TEXT DEFAULT (datetime('now'))
);
CREATE TABLE routes (
  id TEXT PRIMARY KEY,
  crag_id TEXT NOT NULL,
  name TEXT NOT NULL,
  grade TEXT,
  grade_system TEXT DEFAULT 'yds', -- yds, french, v-scale
  height INTEGER, -- meters
  bolt_count INTEGER,
  route_type TEXT CHECK (route_type IN ('sport', 'trad', 'boulder', 'mixed')),
  description TEXT,
  first_ascent TEXT,
  created_at TEXT DEFAULT (datetime('now')),
  FOREIGN KEY (crag_id) REFERENCES crags(id) ON DELETE CASCADE
);
CREATE TABLE galleries (
  id TEXT PRIMARY KEY,
  author_id TEXT NOT NULL,
  title TEXT NOT NULL,
  slug TEXT UNIQUE NOT NULL,
  description TEXT,
  cover_image TEXT,
  is_featured INTEGER DEFAULT 0,
  view_count INTEGER DEFAULT 0,
  created_at TEXT DEFAULT (datetime('now')),
  updated_at TEXT DEFAULT (datetime('now')),
  FOREIGN KEY (author_id) REFERENCES users(id) ON DELETE CASCADE
);
CREATE TABLE gallery_images (
  id TEXT PRIMARY KEY,
  gallery_id TEXT NOT NULL,
  image_url TEXT NOT NULL,
  thumbnail_url TEXT,
  caption TEXT,
  sort_order INTEGER DEFAULT 0,
  created_at TEXT DEFAULT (datetime('now')), location_country TEXT, location_city TEXT, location_spot TEXT,
  FOREIGN KEY (gallery_id) REFERENCES galleries(id) ON DELETE CASCADE
);
CREATE TABLE videos (
  id TEXT PRIMARY KEY,
  title TEXT NOT NULL,
  slug TEXT UNIQUE NOT NULL,
  description TEXT,
  youtube_id TEXT,
  vimeo_id TEXT,
  thumbnail_url TEXT,
  duration INTEGER, -- seconds
  category TEXT,
  tags TEXT, -- JSON array
  is_featured INTEGER DEFAULT 0,
  view_count INTEGER DEFAULT 0,
  published_at TEXT,
  created_at TEXT DEFAULT (datetime('now')),
  updated_at TEXT DEFAULT (datetime('now'))
);
CREATE TABLE biographies (
  id TEXT PRIMARY KEY,
  user_id TEXT,
  name TEXT NOT NULL,
  slug TEXT UNIQUE NOT NULL,
  title TEXT, -- e.g., "Professional Climber"
  bio TEXT,
  avatar_url TEXT,
  cover_image TEXT,
  achievements TEXT, -- JSON array
  social_links TEXT, -- JSON object
  is_featured INTEGER DEFAULT 0,
  created_at TEXT DEFAULT (datetime('now')),
  updated_at TEXT DEFAULT (datetime('now')), climbing_start_year TEXT, frequent_locations TEXT, favorite_route_type TEXT, climbing_reason TEXT, climbing_meaning TEXT, bucket_list TEXT, advice TEXT, is_public INTEGER DEFAULT 0, published_at TEXT, gallery_images TEXT, memorable_moment TEXT, biggest_challenge TEXT, breakthrough_story TEXT, first_outdoor TEXT, first_grade TEXT, frustrating_climb TEXT, fear_management TEXT, climbing_lesson TEXT, failure_perspective TEXT, flow_moment TEXT, life_balance TEXT, unexpected_gain TEXT, climbing_mentor TEXT, climbing_partner TEXT, funny_moment TEXT, favorite_spot TEXT, advice_to_group TEXT, climbing_space TEXT, injury_recovery TEXT, memorable_route TEXT, training_method TEXT, effective_practice TEXT, technique_tip TEXT, gear_choice TEXT, dream_climb TEXT, climbing_trip TEXT, bucket_list_story TEXT, climbing_goal TEXT, climbing_style TEXT, climbing_inspiration TEXT, life_outside_climbing TEXT, youtube_channel_id TEXT, featured_video_id TEXT, total_likes INTEGER DEFAULT 0, total_views INTEGER DEFAULT 0, follower_count INTEGER DEFAULT 0, climbing_origin TEXT, advice_to_self TEXT, comment_count INTEGER DEFAULT 0 NOT NULL, visibility TEXT DEFAULT 'public', tags_data TEXT, one_liners_data TEXT, stories_data TEXT, basic_info_data TEXT, autosave_at TEXT,
  FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE SET NULL
);
CREATE TABLE reviews (
  id TEXT PRIMARY KEY,
  user_id TEXT NOT NULL,
  entity_type TEXT NOT NULL CHECK (entity_type IN ('gym', 'crag')),
  entity_id TEXT NOT NULL,
  rating INTEGER NOT NULL CHECK (rating >= 1 AND rating <= 5),
  content TEXT,
  created_at TEXT DEFAULT (datetime('now')),
  updated_at TEXT DEFAULT (datetime('now')),
  FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE
);
CREATE TABLE d1_migrations(
		id         INTEGER PRIMARY KEY AUTOINCREMENT,
		name       TEXT UNIQUE,
		applied_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL
);
CREATE TABLE likes (
  id TEXT PRIMARY KEY,
  user_id TEXT NOT NULL,
  entity_type TEXT NOT NULL CHECK (entity_type IN ('post', 'gallery', 'video', 'gym', 'crag')),
  entity_id TEXT NOT NULL,
  created_at TEXT DEFAULT (datetime('now')),
  FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE,
  UNIQUE(user_id, entity_type, entity_id)
);
CREATE TABLE bucket_list_items (
  id TEXT PRIMARY KEY,
  biography_id TEXT NOT NULL,

  
  title TEXT NOT NULL,
  category TEXT DEFAULT 'other',
  description TEXT,
  target_grade TEXT,
  target_location TEXT,
  target_date TEXT,

  
  status TEXT DEFAULT 'active',  
  completed_at TEXT,

  
  enable_progress INTEGER DEFAULT 0,
  progress_mode TEXT,  
  progress INTEGER DEFAULT 0,
  milestones TEXT,  

  
  completion_story TEXT,
  psychological_insights TEXT,
  technical_insights TEXT,
  completion_media TEXT,  

  
  is_public INTEGER DEFAULT 1,
  likes_count INTEGER DEFAULT 0,
  inspired_count INTEGER DEFAULT 0,
  comments_count INTEGER DEFAULT 0,

  
  sort_order INTEGER DEFAULT 0,
  created_at TEXT DEFAULT (datetime('now')),
  updated_at TEXT DEFAULT (datetime('now')),

  FOREIGN KEY (biography_id) REFERENCES biographies(id) ON DELETE CASCADE
);
CREATE TABLE biography_videos (
  id TEXT PRIMARY KEY,
  biography_id TEXT NOT NULL,
  video_id TEXT NOT NULL,
  relation_type TEXT NOT NULL CHECK (relation_type IN ('own_content', 'featured_in', 'mentioned', 'recommended', 'completion_proof')),
  is_featured INTEGER DEFAULT 0,
  display_order INTEGER DEFAULT 0,
  created_at TEXT NOT NULL DEFAULT (datetime('now')),

  FOREIGN KEY (biography_id) REFERENCES biographies(id) ON DELETE CASCADE,
  UNIQUE(biography_id, video_id)
);
CREATE TABLE biography_instagrams (
  id TEXT PRIMARY KEY,
  biography_id TEXT NOT NULL,
  instagram_url TEXT NOT NULL,
  instagram_shortcode TEXT NOT NULL,
  media_type TEXT CHECK (media_type IS NULL OR media_type IN ('IMAGE', 'VIDEO', 'CAROUSEL', 'REEL')),
  thumbnail_url TEXT,
  caption TEXT,
  posted_at TEXT,
  relation_type TEXT NOT NULL CHECK (relation_type IN ('own_post', 'tagged', 'mentioned', 'completion_proof')),
  is_featured INTEGER DEFAULT 0,
  display_order INTEGER DEFAULT 0,
  created_at TEXT NOT NULL DEFAULT (datetime('now')),
  updated_at TEXT NOT NULL DEFAULT (datetime('now')),

  FOREIGN KEY (biography_id) REFERENCES biographies(id) ON DELETE CASCADE,
  UNIQUE(biography_id, instagram_shortcode)
);
CREATE TABLE bucket_list_likes (
  id TEXT PRIMARY KEY,
  bucket_list_item_id TEXT NOT NULL,
  user_id TEXT NOT NULL,
  created_at TEXT NOT NULL DEFAULT (datetime('now')),

  FOREIGN KEY (bucket_list_item_id) REFERENCES bucket_list_items(id) ON DELETE CASCADE,
  FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE,
  UNIQUE(bucket_list_item_id, user_id)
);
CREATE TABLE bucket_list_comments (
  id TEXT PRIMARY KEY,
  bucket_list_item_id TEXT NOT NULL,
  user_id TEXT NOT NULL,
  content TEXT NOT NULL,
  created_at TEXT NOT NULL DEFAULT (datetime('now')),

  FOREIGN KEY (bucket_list_item_id) REFERENCES bucket_list_items(id) ON DELETE CASCADE,
  FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE
);
CREATE TABLE bucket_list_references (
  id TEXT PRIMARY KEY,
  source_item_id TEXT NOT NULL,
  target_biography_id TEXT NOT NULL,
  created_at TEXT NOT NULL DEFAULT (datetime('now')),

  FOREIGN KEY (source_item_id) REFERENCES bucket_list_items(id) ON DELETE CASCADE,
  FOREIGN KEY (target_biography_id) REFERENCES biographies(id) ON DELETE CASCADE,
  UNIQUE(source_item_id, target_biography_id)
);
CREATE TABLE follows (
  id TEXT PRIMARY KEY,
  follower_id TEXT NOT NULL,
  following_id TEXT NOT NULL,
  created_at TEXT NOT NULL DEFAULT (datetime('now')),

  FOREIGN KEY (follower_id) REFERENCES users(id) ON DELETE CASCADE,
  FOREIGN KEY (following_id) REFERENCES users(id) ON DELETE CASCADE,
  UNIQUE(follower_id, following_id)
);
CREATE TABLE user_badges (
    id TEXT PRIMARY KEY,
    user_id TEXT NOT NULL,
    badge_id TEXT NOT NULL,
    unlocked_at TEXT NOT NULL DEFAULT (datetime('now')),
    created_at TEXT NOT NULL DEFAULT (datetime('now')),
    FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE,
    UNIQUE(user_id, badge_id)
);
CREATE TABLE story_prompts (
  id TEXT PRIMARY KEY,
  user_id TEXT NOT NULL,
  biography_id TEXT NOT NULL,
  field_name TEXT NOT NULL,           
  category TEXT NOT NULL,             
  prompted_at TEXT NOT NULL DEFAULT (datetime('now')),  
  completed_at TEXT,                  
  dismissed_count INTEGER NOT NULL DEFAULT 0,  
  last_dismissed_at TEXT,             

  FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE,
  FOREIGN KEY (biography_id) REFERENCES biographies(id) ON DELETE CASCADE,
  UNIQUE(biography_id, field_name)
);
CREATE TABLE biography_views (
  id TEXT PRIMARY KEY,
  user_id TEXT NOT NULL,              
  biography_id TEXT NOT NULL,         
  view_count INTEGER NOT NULL DEFAULT 1,  
  first_viewed_at TEXT NOT NULL DEFAULT (datetime('now')),
  last_viewed_at TEXT NOT NULL DEFAULT (datetime('now')),

  FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE,
  FOREIGN KEY (biography_id) REFERENCES biographies(id) ON DELETE CASCADE,
  UNIQUE(user_id, biography_id)
);
CREATE TABLE climbing_locations (
  id TEXT PRIMARY KEY,
  biography_id TEXT NOT NULL,
  location TEXT NOT NULL,             
  country TEXT NOT NULL,              
  visit_year TEXT,                    
  notes TEXT,                         
  photos TEXT,                        
  is_public INTEGER NOT NULL DEFAULT 1,
  sort_order INTEGER NOT NULL DEFAULT 0,
  created_at TEXT NOT NULL DEFAULT (datetime('now')),
  updated_at TEXT NOT NULL DEFAULT (datetime('now')),

  FOREIGN KEY (biography_id) REFERENCES biographies(id) ON DELETE CASCADE
);
CREATE TABLE biography_likes (
  id TEXT PRIMARY KEY,
  biography_id TEXT NOT NULL,
  user_id TEXT NOT NULL,
  created_at TEXT NOT NULL DEFAULT (datetime('now')),

  FOREIGN KEY (biography_id) REFERENCES biographies(id) ON DELETE CASCADE,
  FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE,
  UNIQUE(biography_id, user_id)
);
CREATE TABLE IF NOT EXISTS "comments" (
  id TEXT PRIMARY KEY,
  user_id TEXT NOT NULL,
  entity_type TEXT NOT NULL CHECK (entity_type IN ('post', 'gallery', 'video', 'biography')),
  entity_id TEXT NOT NULL,
  parent_id TEXT,
  content TEXT NOT NULL,
  created_at TEXT DEFAULT (datetime('now')),
  updated_at TEXT DEFAULT (datetime('now')),
  FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE,
  FOREIGN KEY (parent_id) REFERENCES comments(id) ON DELETE CASCADE
);
CREATE TABLE IF NOT EXISTS "notifications" (
  id TEXT PRIMARY KEY,
  user_id TEXT NOT NULL,
  type TEXT NOT NULL CHECK (type IN ('goal_completed', 'goal_liked', 'goal_commented', 'goal_referenced', 'new_follower', 'story_featured', 'biography_commented')),
  actor_id TEXT,
  target_id TEXT,
  title TEXT NOT NULL,
  message TEXT NOT NULL,
  is_read INTEGER NOT NULL DEFAULT 0,
  created_at TEXT NOT NULL DEFAULT (datetime('now')),

  FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE
);
CREATE TABLE bookmarks (
  id TEXT PRIMARY KEY,
  user_id TEXT NOT NULL,
  entity_type TEXT NOT NULL CHECK (entity_type IN ('post', 'gallery', 'video', 'gym', 'crag', 'biography')),
  entity_id TEXT NOT NULL,
  created_at TEXT DEFAULT (datetime('now')),
  FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE,
  UNIQUE(user_id, entity_type, entity_id)
);
PRAGMA foreign_keys=ON;
